import './App.css';
import React, {useState} from "react";
import axios from 'axios';
import Player from "./components/player";


function App() {
  const [userName, setUserName] = useState("");
  const [searchText, setSearchText] = useState("");
  const [gameList, setGameList] = useState([]);


  function handleChange(event){
    const {value} = event.target;
    setSearchText(value);
  }
  function getPlayerGames(event){
    axios.get(`http://localhost:5000/past5games/${searchText}`)
      .then(response => {
        setGameList(response.data);
        setUserName(searchText);
      })
      .catch(error => { 
        console.error(error) 
      });
  }

   console.log(gameList); 

  return (
    <div className="App">
      <h1>Welcome to Proxy Server App</h1>
      <input type = "text" onChange = {handleChange}></input>
      <button onClick={getPlayerGames}>Get past 5 games from player's history</button>
      {gameList.length ? 
        <>
        <h1>Summoner {userName}'s Match History</h1>
        {gameList.map((gameData, index) => {
          return <div><h2>
            Game {index + 1}
          </h2>
          <div>
              {gameData.info.participants.map((data, participantIndex) => {
                // eslint-disable-next-line no-lone-blocks
                {/* return  <div><p>Player {participantIndex + 1}: {data.summonerName}, K/D/A: {data.kills} / {data.deaths} / {data.assists}</p>
                <h1>Champion: {data.championName}</h1> <img src = {`http://ddragon.leagueoflegends.com/cdn/12.9.1/img/champion/${data.championName}.png`} alt = "Champion-Splash-Img"></img>
                <h3>Lane: {data.lane}</h3>
                </div> */}
                return <Player handleClick = {getPlayerGames} props = {data} key = {participantIndex} playerNumber = {participantIndex}/>
              })}
            </div>
            </div>
        })}
        </>
        :
        <>
          <p>We have no data</p>
        </>
      }
      </div>
  );
}

export default App;
