import axios from "axios";
import React, { useState } from "react";



function Player(props){
    const [champMasteryInfo, setChampMasteryInfo] = useState({});
    const { props: {kills, deaths, assists, summonerName, championName, lane, championId, summonerId }} = props;
    const API_CALL = `https://na1.api.riotgames.com/lol/champion-mastery/v4/champion-masteries/by-summoner/${summonerId}/by-champion/${championId}?api_key=RGAPI-24fdd3d9-bad1-41fb-8f69-7c958080b9fe`;

    axios.get(API_CALL)
        .then((response) => {
            setChampMasteryInfo(response.data);
        })
        .catch(error => { return error; });

    let KDA;

    deaths === 0 ? KDA = ((kills + assists)/1).toFixed(2) : KDA = ((kills + assists)/deaths).toFixed(2);
    function handleClick(event){
        event.preventDefault();
        props.handleClick(summonerName);
    }
// const [playerInfo, setPlayerInfo] = useState({});
// useEffect(() => {setPlayerInfo(props)}, 
//     [props]);
// console.log(playerInfo);
return (<div className = "Player">
            <p>Player {props.playerNumber + 1}: <strong>{summonerName}</strong> </p>
            <p>K/D/A:  <strong>{kills}/{deaths}/{assists}</strong>  (<strong>{KDA}</strong> KDA)</p>
            <h1>Champion: {championName} Mastery Level: {champMasteryInfo.championLevel}</h1> 
            <img src = {`http://ddragon.leagueoflegends.com/cdn/12.9.1/img/champion/${championName}.png`} alt = "Champion-Splash-Img"></img>
            <h3>Lane: {lane}</h3>
            <button onClick = {handleClick}> Look Up Player: {summonerName} </button>
        </div>);
}

export default Player;
