// import React, { useState, useEffect } from "react";



function Player(props){
    const { props: {kills, deaths, assists, summonerName, championName, lane }} = props;
    let KDA;
    deaths === 0 ? KDA = ((kills + assists)/1).toFixed(2) : KDA = ((kills + assists)/deaths).toFixed(2);
    function handleClick(event){
        event.preventDefault();
        props.handleClick(summonerName);
    }
// const [playerInfo, setPlayerInfo] = useState({});
// useEffect(() => {setPlayerInfo(props)}, 
//     [props]);
// console.log(playerInfo);
return (<div className = "Player">
            <p>Player {props.playerNumber + 1}: <strong>{summonerName}</strong> </p>
            <p>K/D/A:  <strong>{kills}/{deaths}/{assists}</strong>  (<strong>{KDA}</strong> KDA)</p>
            <h1>Champion: {championName}</h1> <img src = {`http://ddragon.leagueoflegends.com/cdn/12.9.1/img/champion/${championName}.png`} alt = "Champion-Splash-Img"></img>
            <h3>Lane: {lane}</h3>
            <button onClick = {handleClick}> Look Up Player: {summonerName} </button>
        </div>);
}

export default Player;
